import { Component } from "react"

let data = [...new Array(150)].map(() => 0)

let Content = props => {
	let {Chart, event} = props

	const style = {
		display:'flex',
		width: '100%',
		height: '100%',
		background: '#0C0C0C',

		chart: {
			width: '100%', 
			height: '100%',
			background: '#0C0C0C',
			color: 'orange',
			margin: 0,
			padding: 0,
			borderStyle: 'none'
		}
	}

	data.unshift(props.event.data)
	data.pop()

	return <div style={style} >
			<Chart width={500} height={100} data={[0,...data,0]} max={1000} />
		</div>
}

const Histogram = props => {
	let {Component, event, asset, offset, onFocused, onDragged, onClose} = props


	return <Component.Window
			width={400} 
			height={100}
			offset={offset}
			onFocused={onFocused}
			onDragged={onDragged}
			icon={asset.icon}>
		<Content Chart={Component.Lines} event={event} onClose={onClose} />
</Component.Window>


}

export default Histogram
