import {useState} from 'react'

let initialize = (size, max) => [...new Array(size)].map(() => 27)

let nextColas = n => 3*(n % 2 ? n >> 1 : n + 1)
let nextColaz = n => n % 2 ? n >> 1 : 3*n + 1

const barStyle = {
	position: 'absolute'
}

const Content = props => {
	let {Component,selection, state,asset, style, onUpdate} = props

	let [colas, setColas] = useState(initialize(100, 14000))
	let [colaz, setColaz] = useState(initialize(100,14000))
	let [count, setCount] = useState(0)

	let clear = () => {
		setCount(0)
		setColas(initialize(100, 14000))
		setColaz(initialize(100, 14000))
	}

	setInterval(()=> {
		if (count++ > 100){
			clear()
			return
		}

		let clz = nextColaz(colaz[0])
		let cls = nextColas(clz)

		setColaz(data => [clz, ...data.slice(0, data.length-1)])
		setColas(data => [cls, ...data.slice(0, data.length-1)])
	}, 1000)

	return  <div style={style}>
			<div style={{width: '60%'}}>
				<p>Here we proof that the Collatz conjecture is true</p>

				<div>
					<div style={barStyle}>
						<Component.Bars
							data={colas}
							width='500'
							height='100'
							max = '100'
							fill = 'white'
						/>
					</div>

					<div style={barStyle}>
						<Component.Bars
							data={colaz}
							width='500'
							height='100'
							max = '100'
							fill = 'red'
						/>
					</div>
				</div>
			</div>
		</div>
}

export {Content}
