import {useState} from 'react'

let nextColas = n => 3*(n % 2 ? n >> 1 : n + 1)
let nextColaz = n => n % 2 ? n >> 1 : 3*n + 1

let initialize = n => {
	let data = {
		colaz : [n],
		colas : [3*n]
	}
	while (data.colaz[0] > 1){
		let clz = nextColaz(data.colaz[0])

		data.colaz.unshift(clz)
		data.colas.unshift(nextColas(clz))
	}

	return data

}

let data = initialize(27)

const barStyle = {
	position: 'absolute'
}

const Content = props => {
	let {Component,selection, state,asset, style, onUpdate} = props

	return  <div style={style}>
			<div style={{width: '60%'}}>
				<p>Here we proof that the Collatz conjecture is true</p>

				<div>
					<div style={barStyle}>
						<Component.Bars
							data={data.colas}
							width='500'
							height='100'
							max = '100'
							fill = 'white'
						/>
					</div>

					<div style={barStyle}>
						<Component.Bars
							data={data.colaz}
							width='500'
							height='100'
							max = '100'
							fill = 'red'
						/>
					</div>
				</div>
			</div>
		</div>
}

export {Content}
