import {useState} from 'react'

let nextColas = n => 3*(n % 2 ?  n + 1 : n >> 1)
let nextColaz = n => n % 2 ? 3*n + 1 : n >> 1

let initialize = n => {
	let data = {
		colaz : [n],
		colas : [3*n],
		max : -1
	}

	for (let count=0; count < 101; count++ ){
		let clz = nextColaz(data.colaz[0])
		let cls = nextColas(clz)

		data.colaz.unshift(clz)
		data.colas.unshift(cls)
		data.max = Math.max(Math.max(data.max, clz), cls)
	}

	return data

}

const barStyle = {
	position: 'absolute'
}

const Content = props => {
	let {Component,selection, state,asset, style, onUpdate} = props

	let data = initialize(27)

	return  <div style={style}>
			<div style={{width: '60%'}}>
				<p>Here we have a proof that the Collatz conjecture is true</p>

				<div>
					<div style={barStyle}>
						<Component.Bars
							data={data.colas.map(v => Math.log(v))}
							width='700'
							height='300'
							max = {Math.log(data.max)}
							fill = 'white'
						/>
					</div>

					<div style={barStyle}>
						<Component.Bars
							data={data.colaz.map(v => Math.log(v))}
							width='700'
							height='300'
							max = {Math.log(data.max)}
							fill = 'red'
						/>
					</div>
				</div>

				<p>
					The chart is giving a hint of what is going on.<br/>
					Consider the sequence of numbers generated by
				</p>

				<img src={asset.Colas} alt='colas sequence' />
			</div>
		</div>
}

export {Content}
