const Wall = props => {
		let {x,h,z} = props

	return <rect
			x={x}
			y={z}
			width={1}
			height={1}
			fill={h ? 'green' : 'black'} 
		/>

/*return	<mesh position={[x,0,z]}>
		<boxGeometry args={ [1,.4*h,1] } />
		<meshBasicMaterial color={h ? 'green' : 'black'} />
	</mesh>*/

}

let Maze = props => {
	let {data, style} = props

	let width = data[0].length
	let height = data.length

	let scale = {
		x : style.width/width,
		y: style.height/height
	}

	console.log(width, height, scale)

	return <svg style={style} >
			<g transform={`scale(${scale.x},${scale.y})`}>
				<rect width={width} height={height} fill='orange' />
				{data.map( (row, r) => row.map((w,c) => <Wall x={c} h={w} z={r} /> ))}
			</g>
		</svg>
}

export default Maze

