const style = {
	display: 'grid',
	gridTemplateRows: '30px auto 20px',
	height: '100%',
	width: '100%',
	background: '#08093B',
	color: '#BF9000',

	menu: {
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'space-between',
		alignItems: 'center',
		padding: '0 2px 0 2px',
		fontSize: '.7em',

		border: 'solid #BF9000',
		borderWidth: '0 0 1px 0',

		logo : {
			display: 'flex',
			flexDirection: 'row',
			alignItems: 'center',
			width: '100px',
			height: '100%',

			image: {
				height: '80%'
			}
		},

		button: {
			background: '#08093B',
			color: '#BF9000',
			textDecoration: 'none',
			border: 'solid #BF9000 1px',
			borderRadius: '3px',

			width: '100px',
			height: '70%',
			cursor: 'pointer'
		}
	},

	content: {
		display: 'flex',
		height: '100%',

		search: {
			display: 'flex',
			flexDirection: 'column',
			alignItems: 'center',
			width: '100%',

			title: {
				display: 'flex',
				flexDirection: 'row',
				alignItems: 'center',

				height: '30px',
				width: '300px',

				image: {
					height: '20px'
				}
			},

			editor: {
				width: '300px',
				height: '150px',
				background: '#08093B',
				color: '#BF9000',
				resize: 'none',
				marginTop: '6px'
			},

			toolbar: {
				display: 'grid',
				gridTemplateColumns: 'auto 60px',
				width: '300px',
				height: '20px',

				candp: {
					display: 'grid',
					gridTemplateColumns: '20px 10px 20px',
					marginTop: '2px',

					button: {
						height: '20px',
						cursor: 'pointer'
					}
				},

				go: {
					border: 'solid #BF9000 1px',
					borderRadius: '3px',
					cursor: 'pointer',
					height: '20px',
					marginTop: '2px'
				}
			}
		},

		inbox: {
			display: 'flex',
			flexDirection: 'column',
			alignItems: 'center',
			width: '100%',

			title: {
				display: 'flex',
				flexDirection: 'row',
				alignItems: 'center',

				height: '30px',
				width: '300px',

				image: {
					height: '20px'
				}
			},

			editor: {
				width: '300px',
				height: '150px',
				background: '#08093B',
				color: '#BF9000',
				resize: 'none',
				marginTop: '6px'
			},

			toolbar: {
				display: 'grid',
				gridTemplateColumns: 'auto 60px',
				width: '300px',
				height: '20px',
				candp: {
					display: 'grid',
					gridTemplateColumns: '20px 10px 20px',
					marginTop: '2px',

					button: {
						width: '20px',
						cursor: 'pointer'
					}
				}
			}

		}
	},

	copyright: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		fontSize: '.7em',
		paddingLeft: '2px',

		border: 'solid #BF9000',
		borderWidth: '1px 0 0 0'
	}
}

export default style
