import {useState} from 'react'
import Draggable from 'react-draggable'

const Node = props => {
	let {id, label, pos, r, onDragging} = props

	let [shift, setShift] = useState(pos)

	let handleDrag = e => {
		setShift(s => ({x: s.x + e.movementX, y: s.y + e.movementY}) )
		onDragging && onDragging(id)
	}

	return <Draggable 
			position={shift}
			onDrag={handleDrag}>
				<circle
					cx = {r}
					cy = {r}
					r={r}
					fill='black'
					style={{cursor: 'pointer'}}
				/>
		</Draggable>
}

export default Node
