import {useState} from 'react'

import Node from './node'

let position = (w,h) => ({x : Math.floor(Math.random()*w), y: Math.floor(Math.random()*h)})

let organize = (nodes, size) => nodes.map(item => ({...item, pos: position(size.width - 20, size.height - 20)}))

const Network = props => {
	let {style, data, size} = props

	let [layout, setLayout] = useState(size.width && size.height ? organize(data.nodes, size) : organize(data.nodes, {width: 400, height: 400}))

	let handleClick = () => {
		setLayout( organize(data.nodes, size) )
	}

	let handleDragging = id => {
	}

	return <svg style={style} onClick={handleClick}>
			{layout.map(item =>
				size.width && size.height && <Node
					key={item.id}
					id={item.id}
					label={item.name}
					pos={item.pos}
					r = {20}
					onDragging = {handleDragging}
				/>)}
		</svg>
}

export default Network
