const Node = props => {
	let {id, label, pos} = props

	console.log(pos.x, pos.x === 'NaN', pos.x === NaN)
	return !isNaN(pos.x) && !isNaN(pos.y) ? <circle
			cx = {pos.x}
			cy = {pos.y}
			r={20}
			fill='red'
		/>: null
}

let position = (w,h) => ({x : Math.floor(Math.random()*w), y: Math.floor(Math.random()*h)})

const Network = props => {
	let {style, data, size} = props

	return <svg style={style}>
			{data.nodes.map(item => 
				<Node 
					key={item.id} 
					id={item.id} 
					label={item.name}
					pos={position(size.width - 40, size.height - 40)}
				/>)}
		</svg>
}

export default Network
