const ids = [...Array(31)]

const max = list => list.reduce((max,a) => a > max ? a : max)

const Chart = props => {
	let {Bars, data, id, regions} = props

	console.log(id, data)
	const style = {
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'space-between',
		height: '120px'
	}
	return <div style={style}>
			<div>{id}</div>
			{id===8 && <div>{max(data)} , {data}</div> || null}
			{/*<Bars
				data={data}
				width='400'
				height='100'
				max= {max(data)}
				fill='orange'
			/>*/}
			<div>{regions}</div>
		</div>
}

const Monitor = props => {
	let {Component, data, regions, style} = props

	return <div style={style}>
			{ ids.map( (_,id) => <Chart
						Bars = {Component.Bars}
						key={id}
						id={id}
						regions={regions[id]}
						data={data[id]}
					/> )
			}
		</div>
}

export default Monitor
