const Node = props => {
	let {id, label, pos, r} = props

	return <>
		<circle
			cx = {pos.x}
			cy = {pos.y}
			r={r}
			fill='black'
		/>
		<text x={pos.x} y={pos.y} style={{fill: 'white'}} >{label}</text>
		</>
}

let position = (w,h) => ({x : Math.floor(Math.random()*w), y: Math.floor(Math.random()*h)})

const Network = props => {
	let {style, data, size} = props

	return <svg style={style}>
			{data.nodes.map(item => 
				size.width && size.height && <Node
					key={item.id} 
					id={item.id} 
					label={item.name}
					pos={position(size.width - 20, size.height - 20)}
					r = {20}
				/>)}
		</svg>
}

export default Network
