const ids = [...Array(31)].map((_,i) => i)

const max = list => list.reduce((max,a) => a > max ? a : max)

const Chart = props => {
	let {Bars, data, id, regions} = props

	const style = {
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'space-between',
		height: '120px'
	}
	return <div style={style}>
			<div>{id}</div>
			<Bars
				data={data}
				width='400'
				height='100'
				max= {max(data)}
				fill='orange'
			/>
			<div>{regions}</div>
		</div>
}

const Monitor = props => {
	let {Component, state, style} = props

	return <div style={style}>
			{ ids.map( id => <Chart
						Bars = {Component.Bars}
						key={id}
						id={id}
						regions={state.system.regions[id]}
						data={state.system.data[id]}
					/> )
			}
		</div>
}

export default Monitor
