import {useState} from 'react'
import Draggable from 'react-draggable'

const Node = props => {
	let {id, label, pos, r, onDragging} = props

	let handleDrag = e => {
		onDragging && onDragging(id)
	}

	return <Draggable
			defaultPosition={pos}
			onDrag={handleDrag}>
				<circle
					cx = {r}
					cy = {r}
					r={r}
					fill='black'
					style={{cursor: 'pointer'}}
				/>
		</Draggable>
}

export default Node
