import Draggable from 'react-draggable'

const Node = props => {
	let {id, label, pos, r} = props

	return <Draggable style={{cursor: 'pointer'}}>
			<circle
				cx = {pos.x}
				cy = {pos.y}
				r={r}
				fill='black'
			/>
			<text x={pos.x} y={pos.y} style={{fill: 'white'}} >{label}</text>
		</Draggable>
}

export default Node
