import {useState} from 'react'
import Draggable from 'react-draggable'

const Node = props => {
	let {id, label, pos, r} = props

	let [shift, setShift] = useState(pos)

	let handleDrag = e => {
		setShift(s => ({x: s.x + e.movementX, y: s.y + e.movementY}) )

		e.stopPropagation()
	}

	return <Draggable 
			position={shift} 
			onDrag={handleDrag}>
				<circle
					cx = {r}
					cy = {r}
					r={r}
					fill='black'
					style={{cursor: 'pointer'}}
				/>
				{/*<text x={pos.x} y={pos.y} style={{fill: 'white'}} >{label}</text>*/}
		</Draggable>
}

export default Node
