const ids = [...Array(31)]

const max = list => list.reduce((max,a) => a > max ? a : max)

const Chart = props => {
	let {Bars, data, id, regions} = props

	const style = {
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'space-between',
		height: '40px',
		border: '1px solid orange'
	}
	return <div style={style}>
			<div>{id}</div>
			<Bars
				data={data}
				width='200'
				height='60'
				max= {max(data)}
				fill='orange'
			/>
			<div>{regions}</div>
		</div>
}

const Monitor = props => {
	let {Component,event, data, regions, style} = props

	return <div style={style}>
			{ ids.map( (_,id) => <Chart
						Bars = {Component.Bars}
						key={id}
						id={id}
						regions={regions[id]}
						data={data[id]}
					/> )
			}
		</div>
}

export default Monitor
